template.value:
private string $id;

private function __construct()
{
}

public static function fromString(string $id): self
{
    Assert::uuid($id);
    $self = new self();
    $self->id = $id;

    return $self;
}

public function asString(): string
{
    return $this->id;
}

use EventRecordingCapabilities;

private EntityId $id;

private function __construct()
{
}

public static function create(EntityId $id): self
{
    $self = new self();
    $self->id = $id;

    return $self;
}

public function fromState(array $state): self
{
    $self = new self();
    $self->id = EntityId::fromString($state['id']);

    return $self;
}

public function state(): array
{
    return [
        'id' => $this->id->asString(),
    ];
}

private string $title;

private function __construct()
{
}

public static function fromString(string $title): self
{
    Assert::notEmpty($title);
    $self = new self();
    $self->title = $title;

    return $self;
}

public function asString(): string
{
    return $this->title;
}

private array $config;

private function __construct()
{
}

public function build(): array
{
    return [
        'config' => [
            'query_bus' => $this->config,
        ],
    ];
}

public static function withDefaults(): self
{
    $self = new self();
    $self->config = [];

    return $self;
}

private int $id;

private function __construct()
{
}

public static function fromInt(int $id): self
{
    $self = new self();
    $self->id = $id;

    return $self;
}

public function asInt(): int
{
    return $this->id;
}
